import {
  GET_TEACHER_FAILED,
  GET_TEACHER_REQUEST,
  GET_TEACHER_SUCCESS,
} from "./actionType";

const initState = {
  teacher: [],
  isLoading: false,
  isError: false,
};
export const reducer = (state = initState, { type, payload }) => {
  switch (type) {
    case GET_TEACHER_REQUEST:
      return {
        ...state,
        isLoading: true,
        isError: false,
      };
    case GET_TEACHER_SUCCESS:
      return {
        ...state,
        product: payload,
        isLoading: false,
        isError: false,
      };
    case GET_TEACHER_FAILED:
      return {
        ...state,
        isLoading: false,
        isError: true,
      };
    default:
      return state;
  }
};
