import { Button, Container, TextField } from "@material-ui/core";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import MyTable from "../Components/MyTable";
import { getTeacher } from "../Redux/app/action";
import { useHistory } from "react-router-dom";

export default function Dashboard() {
  const history = useHistory();
  const token = useSelector((state) => state.auth.token);
  const teachers = useSelector((state) => state.app.teachers);
  const dispatch = useDispatch();
  const [search, setSearch] = useState("");
  const [gender, setGender] = useState("");
  const [sortByAge, setSortByAge] = useState("");
  const [page, setPage] = useState(1);
  const handleTeacherClick = (id) => {
    history.push(`/teacher/${id}`);
  };
  const handleSearch = () => {
    dispatch(getTeacher(token, search, page));
  };
  useEffect(() => {
    dispatch(getTeacher(token, search, page, sort, order, gender));
  }, [page]);
  return (
    <Container style={{ marginTop: 100, textAlign: "left" }}>
      <TextField
        id="outlined-basic"
        label="Enter name"
        variant="outlined"
        value={search}
        color="secondary"
        onChange={(e) => setSearch(e.target.value)}
      />
      <br />
      <br />
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <Button variant="contained" onClick={handleSearch} color="secondary">
          Search Teacher
        </Button>
        <div>
          <label>Filter By Gender </label>
          <select onChange name="gender">
            <option value="">All</option>
            <option value="saab">Male</option>
            <option value="mercedes">Female</option>
          </select>
          <label>Sort By Age</label>
          <select
            onChange={(e) => {
              setSortByAge(e.target.value);
              setSort("age");
            }}
            name="sort"
          >
            <option value="">Default</option>
            <option value="asc">Ascending</option>
            <option value="dcs">Descending</option>
          </select>
        </div>
      </div>
      <br />
      <br />
      <MyTable teachers={teachers} handleTeacherClick={handleTeacherClick} />
    </Container>
  );
}
